import{c as i,a as s,f as a,w as e,m as o,g as n,o as c,b as l}from"./entry.65199370.js";import{u as r}from"./composables.4f983daf.js";const p=""+globalThis.__publicAssetsURL("image/works/work7/gan-sanpos.jpg"),m=""+globalThis.__publicAssetsURL("image/works/work7/gan-somewhere.jpg"),_=""+globalThis.__publicAssetsURL("image/works/work7/gan-sanpo-ikuta-blackbox.jpg"),d={class:""},u={class:"main-text w-5/6 md:w-1/2 2xl:w-1/3 mx-auto"},g=o('<h1 class="text-3xl pt-5 text-center">GAN散歩</h1><h3 class="text-1xl mt-4 text-center">2022/03-10</h3><div class="flex justify-center"><img class="my-5 rounded-lg" src="'+p+'" alt="ftd"></div><p class="my-5"> “GAN散歩” is a series of articles about walking in an imaginary town generated by GAN (Generative Adversarial Network). There are many examples of GAN learning about landscapes. However, the fact that this article was published in a non-technical web media about walking is also very significant to me. </p><p class="my-5"> ジモトぶらぶらマガジン「サンポー」にて、 GAN（敵対的生成ネットワーク）で生成した実在しない街を散歩するという記事を書いています。 こちらのページは概要のみ紹介しているので、ぜひ記事の方もご覧ください。 </p>',5),h={class:"m-5"},k={class:"list-disc"},x=o('<h2 class="text-2xl mt-10 mb-5">GAN散歩とは</h2><div class="flex justify-center"><img class="my-5 rounded-lg" src="'+m+'" alt="ftd"></div><p class="my-5"> GANとはGenerative Adversarial Network（敵対的生成ネットワーク）の略であり、 生成モデルといわれる機械学習技術の1つです。 よくある使い方として、 大量の画像データを学習させることでそれらの特徴を踏まえた新しい画像を生成することができます。 GAN散歩では、作者の思い入れのある散歩風景から画像データセットを作成し学習、 どこか面影の残る実在しない街を生成してあーだこーだ言いながら散策します。 </p><div class="flex justify-center"><img class="my-5 rounded-lg" src="'+_+'" alt="ftd"></div><p class="my-5"> 例えばこちらの画像。神奈川県川崎市多摩区の生田を学習したAIにより生成された風景です。 全体的にちょっとおかしいですが、特に気になるのは車道に置かれている謎の黒い立方体。 位置的に危ないなあと変な気を回しますが、あちらの世界では道端の植木のように当たり前の存在なのかも。 </p><p class="my-5"> 昨今の画像生成AIはオンライン上の画像を無差別かつ大量に学習しています。 それらに抗うわけでもなく、 個人の趣味趣向に特化し誰の役にも立ちそうにないAIの生成物がお散歩メディアに掲載されている状況に、 自分自身が満足しています。 </p>',6),N={__name:"index",setup(f){return r({title:"GAN散歩 / FTD",meta:[{name:"description",content:"Work by FTD"}]}),(b,w)=>{const t=n;return c(),i("div",d,[s("div",u,[g,s("p",h,[s("ul",k,[s("li",null,[a(t,{class:"my-link",to:"https://sanpoo.jp/article/ikuta-gan-sanpo/",target:"_blank"},{default:e(()=>[l("【GAN散歩】ジェネラティブな生田の潜在空間を探索したい / サンポー")]),_:1})]),s("li",null,[a(t,{class:"my-link",to:"https://sanpoo.jp/article/marumiya-gan-sanpo/",target:"_blank"},{default:e(()=>[l("【GAN散歩】好きな街を混ぜる / サンポー")]),_:1})]),s("li",null,[a(t,{class:"my-link",to:"https://sanpoo.jp/article/inu-gan-sanpo/",target:"_blank"},{default:e(()=>[l("GANで生成した地元の散歩道を犬と歩く / サンポー")]),_:1})])])]),x])])}}};export{N as default};
